import styles from "./Form.module.css";
import { useState } from "react";

const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

function validate(inputs) {
    let errors = {};
    const contra = inputs.password;
    if (!regexEmail.test(inputs.username)) {
        errors.username = "Debe ser un correo electr칩nico valido";
    }
    if (contra.length < 6 || contra.length > 10 || !(/\d/.test(contra))) {
        errors.password = "La contrase침a debe tener entre 6 y 10 caracteres y al menos un numero";
    }
    return errors;
}

export default function Form() {
    const [inputs, setInputs] = useState({
        username: "",
        password: "",
    });
    const [errors, setErrors] = useState({
        username: "",
        password: "",
    });

    const handleChange = (event) => {
        setInputs({...inputs,
            [event.target.name]: event.target.value
        });
        setErrors(validate(inputs));
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        if (Object.values(errors).length === 0) {
            alert("Datos completos")
            setErrors({username: "", password: ""});
            setInputs({username: "", password: ""});
        } else alert("Te fallan los datos, maestro/a");
    }

    return (
        <div>
        <form className={styles.Formulario} onSubmit={handleSubmit}>
            <label>Username: 
                <input
                type="text"
                name="username"
                value={inputs.username}
                placeholder="Usuario..."
                onChange={handleChange}
                className={errors.username && styles.Warning}
                >

                </input>
                {errors.username && <p className={styles.Danger}>{errors.username}</p>}
            </label>
            <label>Password: 
                <input
                    type="text"
                    name="password"
                    value={inputs.password}
                    placeholder="Password..."
                    onChange={handleChange}
                    className={errors.password && styles.Warning}>

                </input>
                {errors.password && <p className={styles.Danger}>{errors.password}</p>}
            </label>
            <button type="submit">confirmar</button>
        </form>
        </div>
    );
}
